        <h3 className="text-muted">
          Sleek,modern and intuitive trading platforms
        </h3>
        <h6 className="text-muted">
          check out our <br />
          <a href="" style={{ textDecoration: "none" }}>
            investment offerings
            <i class="fa fa-long-arrow-right" aria-hidden="true"></i>
          </a>
        </h6>
      </div>
    </div>
  );
}

export default Hero;
